HOMEViewBase::~HOMEViewBase()
{
    touchgfx::CanvasWidgetRenderer::resetBuffer();
}

void HOMEViewBase::setupScreen()
{

}

void HOMEViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &buttonWithLabel1)
    {
        //Interaction2
        //When buttonWithLabel1 clicked change screen to SETTINGS_MENU
        //Go to SETTINGS_MENU with no screen transition
        application().gotoSETTINGS_MENUScreenNoTransition();
    }
    if (&src == &buttonWithLabel1_1)
    {
        //Interaction3
        //When buttonWithLabel1_1 clicked change screen to MANUAL
        //Go to MANUAL with no screen transition
        application().gotoMANUALScreenNoTransition();
    }
    if (&src == &buttonWithLabel1_1_1)
    {
        //Interaction4
        //When buttonWithLabel1_1_1 clicked change screen to MONITOR
        //Go to MONITOR with no screen transition
        application().gotoMONITORScreenNoTransition();
    }
}

void HOMEViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &SET_WORK_HEIGHT)
    {
        //SET_WORK_HEIGHT
        //When SET_WORK_HEIGHT clicked change screen to SETTINGS_WORK_POSITION
        //Go to SETTINGS_WORK_POSITION with screen transition towards East
        application().gotoSETTINGS_WORK_POSITIONScreenSlideTransitionEast();
    }
    if (&src == &HOME_TIP1_SETTINGS)
    {
        //Interaction7
        //When HOME_TIP1_SETTINGS clicked change screen to SETTINGS
        //Go to SETTINGS with no screen transition
        application().gotoSETTINGSScreenNoTransition();
    }
    if (&src == &HOME_TIP4_SETTINGS)
    {
        //Interaction10
        //When HOME_TIP4_SETTINGS clicked change screen to TIP_4
        //Go to TIP_4 with screen transition towards East
        application().gotoTIP_4ScreenSlideTransitionEast();
    }
}
