void SETTINGSViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &flexButton1)
    {
        //Interaction1
        //When flexButton1 clicked change screen to HOME
        //Go to HOME with no screen transition
        application().gotoHOMEScreenNoTransition();
    }
    if (&src == &HEAT_SETTINGS_BACK)
    {
        //Interaction5
        //When HEAT_SETTINGS_BACK clicked change screen to SETTINGS_MENU
        //Go to SETTINGS_MENU with screen transition towards East
        application().gotoSETTINGS_MENUScreenSlideTransitionEast();
    }
}

void SETTINGSViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &TIP_1_SETTINGS_BUTTON)
    {
        //Interaction2
        //When TIP_1_SETTINGS_BUTTON clicked change screen to TIP_1
        //Go to TIP_1 with no screen transition
        application().gotoTIP_1ScreenNoTransition();
    }
    if (&src == &TIP_2_SETTINGS_BUTTON)
    {
        //Interaction3
        //When TIP_2_SETTINGS_BUTTON clicked change screen to TIP_2
        //Go to TIP_2 with no screen transition
        application().gotoTIP_2ScreenNoTransition();
    }
    if (&src == &TIP_3_SETTINGS_BUTTON)
    {
        //Interaction4
        //When TIP_3_SETTINGS_BUTTON clicked change screen to TIP_3
        //Go to TIP_3 with no screen transition
        application().gotoTIP_3ScreenNoTransition();
    }
    if (&src == &TIP_4_SETTINGS_BUTTON)
    {
        //Interaction6
        //When TIP_4_SETTINGS_BUTTON clicked change screen to TIP_4
        //Go to TIP_4 with screen transition towards East
        application().gotoTIP_4ScreenSlideTransitionEast();
    }
}
